import react, { useState } from "react";
import "./App.css";

const App = () => {
  const [result, setResult] = useState("");
  const handleClick = (e) => {
    setResult(result.concat(e.target.name));
  };
  const getPercent = () => {
    setResult(eval(result / 100).toString());
  };
  const getResult = () => {
    setResult(eval(result).toString());
  };
  const clearInput = () => {
    setResult("");
  };
  return (
    <div className="container">
      <form>
        <input type="text" value={result} />
      </form>

      <div className="keypad">
        <button onClick={clearInput} className="highlighted" id="clear">
          Clear
        </button>
        <button onClick={backspace} className="highlighted" id="backspace">
          âŒ«{" "}
        </button>
        <button onClick={getPercent} name="%" className="highlighted">
          &#37;
        </button>
        <button name="/" className="highlighted" onClick={handleClick}>
          &divide;
        </button>
        <button name="7" onClick={handleClick}>
          7
        </button>
        <button name="8" onClick={handleClick}>
          8
        </button>
        <button name="9" onClick={handleClick}>
          9
        </button>
        <button name="x" className="highlighted" onClick={handleClick}>
          &times;
        </button>
        <button name="4" onClick={handleClick}>
          4
        </button>
        <button name="5" onClick={handleClick}>
          5
        </button>
        <button name="6" onClick={handleClick}>
          6
        </button>
        <button name="-" className="highlighted" onClick={handleClick}>
          &ndash;
        </button>
        <button name="1" onClick={handleClick}>
          1
        </button>
        <button name="2" onClick={handleClick}>
          2
        </button>
        <button name="3" onClick={handleClick}>
          3
        </button>
        <button name="+" className="highlighted" onClick={handleClick}>
          &#10010;
        </button>
        <button name="." onClick={handleClick}>
          .
        </button>
        <button name="0" onClick={handleClick}>
          0
        </button>
        <button onClick={getResult} className="major" id="result">
          =
        </button>
      </div>
    </div>
  );
};

export default App;
