import react, { useState } from "react";
import "./App.css";

const App = () => {
  const [result, setResult] = useState("");
  const click = (e) => {
    setResult(result.concat(e.target.name));
  };
  const getPercent = () => {
    setResult(eval(result / 100).toString());
  };
  const getResult = () => {
    setResult(eval(result).toString());
  };
  const clearInput = () => {
    setResult("");
  };

  const backspace = () => {
    setResult(result.slice(0, result.length - 1));
  };

  return (
    <div className="container-dark">
      <form>
        <input type="text" className="input-dark" value={result} />
      </form>

      <div className="keypad">
        <button
          onClick={clearInput}
          className="highlighted-dark darkTheme-btn"
          id="clear"
        >
          Clear
        </button>
        <button
          onClick={backspace}
          className="highlighted-dark darkTheme-btn"
          id="backspace"
        >
          ⌫
        </button>
        <button
          onClick={getPercent}
          name="%"
          className="highlighted-dark darkTheme-btn"
        >
          &#37;
        </button>
        <button
          name="/"
          className="highlighted-dark darkTheme-btn"
          onClick={click}
        >
          &divide;
        </button>
        <button name="7" className="darkTheme-btn" onClick={click}>
          7
        </button>
        <button name="8" className="darkTheme-btn" onClick={click}>
          8
        </button>
        <button name="9" className="darkTheme-btn" onClick={click}>
          9
        </button>
        <button
          name="x"
          className="highlighted-dark darkTheme-btn"
          onClick={click}
        >
          &times;
        </button>
        <button name="4" className="darkTheme-btn" onClick={click}>
          4
        </button>
        <button name="5" className="darkTheme-btn" onClick={click}>
          5
        </button>
        <button name="6" className="darkTheme-btn" onClick={click}>
          6
        </button>
        <button
          name="-"
          className="highlighted-dark darkTheme-btn"
          onClick={click}
        >
          &ndash;
        </button>
        <button name="1" className="darkTheme-btn" onClick={click}>
          1
        </button>
        <button name="2" className="darkTheme-btn" onClick={click}>
          2
        </button>
        <button name="3" className="darkTheme-btn" onClick={click}>
          3
        </button>
        <button
          name="+"
          className="highlighted-dark darkTheme-btn"
          onClick={click}
        >
          &#10010;
        </button>
        <button name="." className="darkTheme-btn" onClick={click}>
          .
        </button>
        <button name="0" className="darkTheme-btn" onClick={click}>
          0
        </button>
        <button onClick={getResult} className="major darkTheme-btn" id="result">
          =
        </button>
      </div>
    </div>
  );
};

export default App;
