import react, { useState } from "react";
import "./App.css";

const App = () => {
  const [result, setResult] = useState("");
  const click = (e) => {
    setResult(result.concat(e.target.name));
  };
  const getPercent = () => {
    setResult(eval(result / 100).toString());
  };
  const getResult = () => {
    setResult(eval(result).toString());
  };
  const clearInput = () => {
    setResult("");
  };

  const backspace = () => {
    setResult(result.slice(0, result.length - 1));
  };
  return (
    <div className="container">
      <form>
        <input type="text" value={result} />
      </form>

      <div className="keypad">
        <button onClick={clearInput} className="highlighted" id="clear">
          Clear
        </button>
        <button onClick={backspace} className="highlighted" id="backspace">
          âŒ«
        </button>
        <button onClick={getPercent} name="%" className="highlighted">
          &#37;
        </button>
        <button name="/" className="highlighted" onClick={click}>
          &divide;
        </button>
        <button name="7" className="" onClick={click}>
          7
        </button>
        <button name="8" onClick={click}>
          8
        </button>
        <button name="9" onClick={click}>
          9
        </button>
        <button name="x" className="highlighted" onClick={click}>
          &times;
        </button>
        <button name="4" onClick={click}>
          4
        </button>
        <button name="5" onClick={click}>
          5
        </button>
        <button name="6" onClick={click}>
          6
        </button>
        <button name="-" className="highlighted" onClick={click}>
          &ndash;
        </button>
        <button name="1" onClick={click}>
          1
        </button>
        <button name="2" onClick={click}>
          2
        </button>
        <button name="3" onClick={click}>
          3
        </button>
        <button name="+" className="highlighted" onClick={click}>
          &#10010;
        </button>
        <button name="." onClick={click}>
          .
        </button>
        <button name="0" onClick={click}>
          0
        </button>
        <button onClick={getResult} className="major" id="result">
          =
        </button>
      </div>
    </div>
  );
};

export default App;
