import React, { Component } from "react";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      result: "",
    };
  }
  click = (e) => {
    this.setState(this.state.result + e.currentTarget.value);
  };
  getPercent = () => {
    this.setState(eval(this.result / 100).toString());
  };
  getResult = () => {
    this.setState(eval(this.result).toString());
  };
  clearInput = () => {
    this.setState("");
  };

  backspace = () => {
    this.setState(this.result.slice(0, this.result.length - 1));
  };

  render() {
    return (
      <div className="container-dark">
        <form>
          <input type="text" className="input-dark" value={this.state.result} />
        </form>

        <div className="keypad">
          <button
            onClick={this.clearInput}
            className="highlighted-dark darkTheme-btn"
            id="clear"
          >
            Clear
          </button>
          <button
            onClick={this.backspace}
            className="highlighted-dark darkTheme-btn"
            id="backspace"
          >
            âŒ«
          </button>
          <button
            onClick={this.getPercent}
            name="%"
            className="highlighted-dark darkTheme-btn"
          >
            &#37;
          </button>
          <button
            name="/"
            className="highlighted-dark darkTheme-btn"
            onClick={this.click()}
          >
            &divide;
          </button>
          <button name="7" className="darkTheme-btn" onClick={this.click}>
            7
          </button>
          <button name="8" className="darkTheme-btn" onClick={this.click}>
            8
          </button>
          <button name="9" className="darkTheme-btn" onClick={this.click}>
            9
          </button>
          <button
            name="x"
            className="highlighted-dark darkTheme-btn"
            onClick={this.click}
          >
            &times;
          </button>
          <button name="4" className="darkTheme-btn" onClick={this.click}>
            4
          </button>
          <button name="5" className="darkTheme-btn" onClick={this.click}>
            5
          </button>
          <button name="6" className="darkTheme-btn" onClick={this.click}>
            6
          </button>
          <button
            name="-"
            className="highlighted-dark darkTheme-btn"
            onClick={this.click}
          >
            &ndash;
          </button>
          <button name="1" className="darkTheme-btn" onClick={this.click}>
            1
          </button>
          <button name="2" className="darkTheme-btn" onClick={this.click}>
            2
          </button>
          <button name="3" className="darkTheme-btn" onClick={this.click}>
            3
          </button>
          <button
            name="+"
            className="highlighted-dark darkTheme-btn"
            onClick={this.click}
          >
            &#10010;
          </button>
          <button name="." className="darkTheme-btn" onClick={this.click}>
            .
          </button>
          <button name="0" className="darkTheme-btn" onClick={this.click}>
            0
          </button>
          <button
            onClick={this.getResult}
            className="major darkTheme-btn"
            id="result"
          >
            =
          </button>
        </div>
      </div>
    );
  }
}
